import React, { useState } from "react";

const AdvancedPromptGenerator = () => {
  // Core settings
  const [category, setCategory] = useState("Fantasy");
  const [style, setStyle] = useState("Realistic");
  const [subject, setSubject] = useState("");
  const [mood, setMood] = useState("Mysterious");
  const [composition, setComposition] = useState("Centered");
  
  // Visual settings
  const [lighting, setLighting] = useState("Soft");
  const [colorScheme, setColorScheme] = useState("Vibrant");
  const [details, setDetails] = useState("High Detail");
  const [aspectRatio, setAspectRatio] = useState("16:9");
  
  // Camera settings
  const [showCamera, setShowCamera] = useState(false);
  const [camera, setCamera] = useState("");
  const [lens, setLens] = useState("");
  const [focalLength, setFocalLength] = useState("");
  const [aperture, setAperture] = useState("");
  const [shutterSpeed, setShutterSpeed] = useState("");
  const [iso, setIso] = useState("");
  const [effect, setEffect] = useState("");
  
  // Advanced settings
  const [showAdvanced, setShowAdvanced] = useState(false);
  const [extraDetails, setExtraDetails] = useState("");
  const [negativePrompt, setNegativePrompt] = useState("");
  const [selectedArtists, setSelectedArtists] = useState([]);
  const [selectedArtistInfo, setSelectedArtistInfo] = useState(null);
  
  // Output
  const [generatedPrompt, setGeneratedPrompt] = useState("");
  const [savedPrompts, setSavedPrompts] = useState([]);

  const artists = [
    { 
      name: "Claude Monet", 
      style: "Impressionist style with emphasis on light and color",
      image: "https://upload.wikimedia.org/wikipedia/commons/a/a4/Claude_Monet_-_Water_Lilies_-_Google_Art_Project.jpg"
    },
    { 
      name: "Vincent van Gogh", 
      style: "Post-impressionist style with bold colors and expressive brushstrokes",
      image: "https://upload.wikimedia.org/wikipedia/commons/e/ea/Van_Gogh_-_Starry_Night_-_Google_Art_Project.jpg"
    },
    { 
      name: "Leonardo da Vinci", 
      style: "Renaissance style with precise detail and sfumato technique",
      image: "https://upload.wikimedia.org/wikipedia/commons/e/ec/Mona_Lisa%2C_by_Leonardo_da_Vinci%2C_from_C2RMF_retouched.jpg"
    },
    { 
      name: "Salvador DalÃ­", 
      style: "Surrealist style with dreamlike and bizarre imagery",
      image: "https://upload.wikimedia.org/wikipedia/en/1/1b/Salvador_Dal%C3%AD_-_The_Elephants.jpg"
    },
    { 
      name: "Andy Warhol", 
      style: "Pop art style with bold colors and repeated images",
      image: "https://upload.wikimedia.org/wikipedia/en/1/1f/Campbells_Soup_Cans_MOMA.jpg"
    },
    { 
      name: "Gustav Klimt", 
      style: "Art Nouveau style with ornate patterns and gold leaf",
      image: "https://upload.wikimedia.org/wikipedia/commons/8/84/The_Kiss_-_Gustav_Klimt_-_Google_Cultural_Institute.jpg"
    },
    { 
      name: "Frida Kahlo", 
      style: "Mexican folk art style with bold colors and symbolic elements",
      image: "https://upload.wikimedia.org/wikipedia/en/1/1e/Frida_Kahlo_%28self_portrait%29.jpg"
    },
    { 
      name: "Pablo Picasso", 
      style: "Cubist style with geometric forms and multiple perspectives",
      image: "https://upload.wikimedia.org/wikipedia/en/7/74/PicassoGuernica.jpg"
    },
    { 
      name: "Rembrandt", 
      style: "Baroque style with dramatic lighting and rich textures",
      image: "https://upload.wikimedia.org/wikipedia/commons/3/38/Rembrandt_-_The_Night_Watch_-_Google_Art_Project.jpg"
    },
    { 
      name: "Hokusai", 
      style: "Japanese ukiyo-e style with precise lines and flat colors",
      image: "https://upload.wikimedia.org/wikipedia/commons/a/a5/Tsunami_by_hokusai_19th_century.jpg"
    }
  ];

  const toggleArtist = (artist) => {
    setSelectedArtists(prev => 
      prev.includes(artist.name)
        ? prev.filter(name => name !== artist.name)
        : [...prev, artist.name]
    );
  };

  const showArtistInfo = (artist) => {
    setSelectedArtistInfo(artist);
  };

  const generatePrompt = () => {
    let prompt = `${category} ${style} of ${subject}`;
    
    if (mood) prompt += `, ${mood} mood`;
    if (composition) prompt += `, ${composition} composition`;
    if (lighting) prompt += `, ${lighting} lighting`;
    if (colorScheme) prompt += `, ${colorScheme} colors`;
    if (details) prompt += `, ${details}`;
    
    if (showCamera) {
      if (camera) prompt += `, shot on ${camera}`;
      if (lens) prompt += ` with ${lens}`;
      if (focalLength) prompt += ` at ${focalLength}`;
      if (aperture) prompt += `, f/${aperture}`;
      if (shutterSpeed) prompt += `, ${shutterSpeed}`;
      if (iso) prompt += `, ISO ${iso}`;
      if (effect) prompt += `, ${effect} effect`;
    }
    
    if (selectedArtists.length > 0) {
      prompt += `, in the style of ${selectedArtists.join(" and ")}`;
    }
    
    if (extraDetails) prompt += `, ${extraDetails}`;
    
    setGeneratedPrompt(prompt);
  };

  // Add to state declarations
  const [promptHistory, setPromptHistory] = useState([]);

  // Modify savePrompt function
  const savePrompt = () => {
    if (generatedPrompt) {
      const historyEntry = {
        prompt: generatedPrompt,
        negative: negativePrompt,
        timestamp: new Date().toISOString(),
        settings: {
          category,
          style,
          mood,
          lighting,
          colorScheme,
          selectedArtists,
          camera,
          lens,
          // ... other settings
        }
      };
      setPromptHistory(prev => [historyEntry, ...prev]);
      setSavedPrompts(prev => [...prev, generatedPrompt]);
    }
  };

  const copyToClipboard = () => {
    if (generatedPrompt) {
      navigator.clipboard.writeText(generatedPrompt);
    }
  };

  const clearPrompt = () => {
    setGeneratedPrompt("");
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4 text-gray-800">Advanced Prompt Generator</h1>
      
      {/* Core Settings */}
      <div className="mb-4">
        <h2 className="font-semibold text-lg mb-2 text-gray-700">Core Settings</h2>
        <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
          <div>
            <label className="block text-sm text-gray-600 mb-1">Category</label>
            <select 
              className="w-full border p-2 rounded" 
              value={category} 
              onChange={(e) => setCategory(e.target.value)}
            >
              <option>Fantasy</option>
              <option>Sci-Fi</option>
              <option>Nature</option>
              <option>Portrait</option>
              <option>Architecture</option>
              <option>Abstract</option>
              <option>Still Life</option>
              <option>Landscape</option>
            </select>
          </div>
          <div>
            <label className="block text-sm text-gray-600 mb-1">Style</label>
            <select 
              className="w-full border p-2 rounded" 
              value={style} 
              onChange={(e) => setStyle(e.target.value)}
            >
              <option>Realistic</option>
              <option>Cartoon</option>
              <option>Anime</option>
              <option>Digital Art</option>
              <option>Oil Painting</option>
              <option>Watercolor</option>
              <option>Sketch</option>
              <option>3D Render</option>
            </select>
          </div>
          <div>
            <label className="block text-sm text-gray-600 mb-1">Subject</label>
            <input
              type="text"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              placeholder="Main subject of the image"
              className="w-full border p-2 rounded"
            />
          </div>
          <div>
            <label className="block text-sm text-gray-600 mb-1">Mood</label>
            <select 
              className="w-full border p-2 rounded" 
              value={mood} 
              onChange={(e) => setMood(e.target.value)}
            >
              <option>Mysterious</option>
              <option>Peaceful</option>
              <option>Dark</option>
              <option>Energetic</option>
              <option>Romantic</option>
              <option>Melancholic</option>
              <option>Whimsical</option>
              <option>Epic</option>
            </select>
          </div>
          <div>
            <label className="block text-sm text-gray-600 mb-1">Composition</label>
            <select 
              className="w-full border p-2 rounded" 
              value={composition} 
              onChange={(e) => setComposition(e.target.value)}
            >
              <option>Centered</option>
              <option>Rule of Thirds</option>
              <option>Dynamic</option>
              <option>Symmetrical</option>
              <option>Close-up</option>
              <option>Wide Shot</option>
              <option>Bird's Eye View</option>
              <option>Worm's Eye View</option>
            </select>
          </div>
        </div>
      </div>
      
      {/* Camera Settings Toggle */}
      <div className="mb-4">
        <button 
          onClick={() => {
            console.log('Toggle camera:', !showCamera); // Add this line for debugging
            setShowCamera(!showCamera);
          }}
          className="text-blue-500 hover:text-blue-600 font-medium"
        >
          {showCamera ? "Hide Camera Settings â¼" : "Show Camera Settings â¶"}
        </button>
      </div>
      
      {/* Camera Settings */}
      {showCamera && (
        <div className="mb-4 p-3 bg-gray-50 rounded border">
          <h2 className="font-semibold text-lg mb-2 text-gray-700">Camera Settings</h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
            <div>
              <label className="block text-sm text-gray-600 mb-1">Camera</label>
              <select 
                className="w-full border p-2 rounded" 
                value={camera} 
                onChange={(e) => setCamera(e.target.value)}
              >
                <option value="">Select Camera</option>
                <option>Canon EOS R5</option>
                <option>Sony A7R IV</option>
                <option>Nikon Z9</option>
                <option>Fujifilm X-T4</option>
                <option>Hasselblad X1D II</option>
              </select>
            </div>
            <div>
              <label className="block text-sm text-gray-600 mb-1">Lens</label>
              <select 
                className="w-full border p-2 rounded" 
                value={lens} 
                onChange={(e) => setLens(e.target.value)}
              >
                <option value="">Select Lens</option>
                <option>50mm f/1.4</option>
                <option>24-70mm f/2.8</option>
                <option>70-200mm f/2.8</option>
                <option>85mm f/1.2</option>
                <option>35mm f/1.4</option>
              </select>
            </div>
            <div>
              <label className="block text-sm text-gray-600 mb-1">Focal Length</label>
              <select 
                className="w-full border p-2 rounded" 
                value={focalLength} 
                onChange={(e) => setFocalLength(e.target.value)}
              >
                <option value="">Select Length</option>
                <option>24mm</option>
                <option>35mm</option>
                <option>50mm</option>
                <option>85mm</option>
                <option>135mm</option>
              </select>
            </div>
            <div>
              <label className="block text-sm text-gray-600 mb-1">Aperture</label>
              <select 
                className="w-full border p-2 rounded" 
                value={aperture} 
                onChange={(e) => setAperture(e.target.value)}
              >
                <option value="">Select f-stop</option>
                <option>1.4</option>
                <option>2.8</option>
                <option>4</option>
                <option>5.6</option>
                <option>8</option>
                <option>11</option>
              </select>
            </div>
            <div>
              <label className="block text-sm text-gray-600 mb-1">Shutter Speed</label>
              <select 
                className="w-full border p-2 rounded" 
                value={shutterSpeed} 
                onChange={(e) => setShutterSpeed(e.target.value)}
              >
                <option value="">Select Speed</option>
                <option>1/1000</option>
                <option>1/500</option>
                <option>1/250</option>
                <option>1/125</option>
                <option>1/60</option>
                <option>1/30</option>
              </select>
            </div>
            <div>
              <label className="block text-sm text-gray-600 mb-1">ISO</label>
              <select 
                className="w-full border p-2 rounded" 
                value={iso} 
                onChange={(e) => setIso(e.target.value)}
              >
                <option value="">Select ISO</option>
                <option>100</option>
                <option>200</option>
                <option>400</option>
                <option>800</option>
                <option>1600</option>
                <option>3200</option>
              </select>
            </div>
            <div>
              <label className="block text-sm text-gray-600 mb-1">Effect</label>
              <select 
                className="w-full border p-2 rounded" 
                value={effect} 
                onChange={(e) => setEffect(e.target.value)}
              >
                <option value="">Select Effect</option>
                <option>depth of field</option>
                <option>motion blur</option>
                <option>HDR</option>
                <option>long exposure</option>
              </select>
            </div>
          </div>
          
          <div className="mt-2 text-xs p-2 bg-blue-50 rounded border border-blue-100 text-blue-700">
            <strong>Tip:</strong> Using specific camera gear can enhance realism. For artistic images, consider leaving these unspecified.
          </div>
        </div>
      )}
      
      {/* Visual Settings */}
      <div className="mb-2">
        <h2 className="font-semibold text-lg mb-2 text-gray-700">Visual Settings</h2>
        <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
          <div>
            <label className="block text-sm text-gray-600 mb-1">Lighting</label>
            <select 
              className="w-full border p-2 rounded" 
              value={lighting} 
              onChange={(e) => setLighting(e.target.value)}
            >
              <option>Soft</option>
              <option>Dramatic</option>
              <option>Neon</option>
              <option>Natural</option>
              <option>Backlit</option>
              <option>Rim lighting</option>
              <option>Volumetric</option>
              <option>Studio lighting</option>
              <option>Cinematic lighting</option>
              <option>Candlelight</option>
            </select>
          </div>
          <div>
            <label className="block text-sm text-gray-600 mb-1">Color Scheme</label>
            <select 
              className="w-full border p-2 rounded" 
              value={colorScheme} 
              onChange={(e) => setColorScheme(e.target.value)}
            >
              <option>Vibrant</option>
              <option>Pastel</option>
              <option>Monochrome</option>
              <option>Dark Tones</option>
              <option>Complementary</option>
              <option>Analogous</option>
              <option>Triadic</option>
              <option>Sepia</option>
              <option>Neon</option>
              <option>Muted</option>
            </select>
          </div>
          <div>
            <label className="block text-sm text-gray-600 mb-1">Details</label>
            <select 
              className="w-full border p-2 rounded" 
              value={details} 
              onChange={(e) => setDetails(e.target.value)}
            >
              <option>High Detail</option>
              <option>Minimalist</option>
              <option>Sketchy</option>
              <option>Photorealistic</option>
              <option>Intricate</option>
              <option>Stylized</option>
              <option>Low poly</option>
              <option>Impressionist</option>
              <option>Hyper-detailed</option>
              <option>Retro</option>
            </select>
          </div>
          <div>
            <label className="block text-sm text-gray-600 mb-1">Aspect Ratio</label>
            <select 
              className="w-full border p-2 rounded" 
              value={aspectRatio} 
              onChange={(e) => setAspectRatio(e.target.value)}
            >
              <option>16:9</option>
              <option>1:1</option>
              <option>4:3</option>
              <option>9:16</option>
              <option>3:2</option>
              <option>2:3</option>
              <option>21:9</option>
              <option>5:4</option>
            </select>
          </div>
        </div>
      </div>
      
      {/* Advanced Settings */}
      <div className="mb-4">
        <button 
          onClick={() => setShowAdvanced(!showAdvanced)}
          className="text-blue-500 hover:text-blue-600 font-medium"
        >
          {showAdvanced ? "Hide Advanced Settings â¼" : "Show Advanced Settings â¶"}
        </button>
      </div>
      
      {/* Advanced Settings Content */}
      {showAdvanced && (
        <div className="mb-2 p-3 bg-gray-50 rounded border">
          <h2 className="font-semibold text-lg mb-2 text-gray-700">Advanced Settings</h2>
          
          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Artist Inspiration</label>
            <p className="text-xs text-gray-500 mb-2">Click an artist to add to your prompt. Hover/tap to see style description.</p>
            <div className="flex flex-wrap gap-2 mb-2">
              {artists.map((artist) => (
                <div key={artist.name} className="relative inline-block">
                  <button
                    onClick={() => toggleArtist(artist)}
                    onMouseEnter={() => showArtistInfo(artist)}
                    onMouseLeave={() => setSelectedArtistInfo(null)}
                    className={`text-xs px-2 py-1 rounded transition-colors ${
                      selectedArtists.includes(artist.name) 
                        ? 'bg-blue-500 text-white' 
                        : 'bg-gray-100 hover:bg-gray-200 text-gray-800'
                    }`}
                  >
                    {artist.name}
                    <span className="ml-1 text-xs cursor-help">â¹ï¸</span>
                  </button>
                  
                  {selectedArtistInfo && selectedArtistInfo.name === artist.name && (
                    <div className="absolute z-10 w-64 p-2 mt-1 text-xs bg-white rounded shadow-lg border border-gray-200">
                      <div className="flex flex-col mb-1">
                        <div className="w-full">
                          <strong>{artist.name}</strong>
                          <p className="mb-2">{artist.style}</p>
                          {artist.image && (
                            <img 
                              src={artist.image} 
                              alt={`Style example of ${artist.name}`} 
                              className="w-full h-32 object-cover rounded"
                            />
                          )}
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
          
          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Additional Details</label>
            <input
              type="text"
              value={extraDetails}
              onChange={(e) => setExtraDetails(e.target.value)}
              placeholder="Add any specific details (materials, techniques, elements, etc.)"
              className="w-full p-2 border rounded"
            />
          </div>
          
          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Negative Prompt (things to avoid)</label>
            <input
              type="text"
              value={negativePrompt}
              onChange={(e) => setNegativePrompt(e.target.value)}
              placeholder="Elements to avoid (e.g., 'blurry, distorted faces')"
              className="w-full p-2 border rounded"
            />
          </div>
        </div>
      )}
      
      {/* Buttons */}
      <div className="flex flex-wrap gap-2">
        <button 
          onClick={generatePrompt} 
          className="flex-grow bg-blue-500 hover:bg-blue-600 text-white p-2 rounded transition-colors"
        >
          Generate Prompt
        </button>
        <button 
          onClick={savePrompt} 
          disabled={!generatedPrompt}
          className={`px-4 py-2 rounded transition-colors ${
            generatedPrompt 
              ? 'bg-green-500 hover:bg-green-600 text-white' 
              : 'bg-gray-200 text-gray-500 cursor-not-allowed'
          }`}
        >
          Save
        </button>
        <button 
          onClick={copyToClipboard} 
          disabled={!generatedPrompt}
          className={`px-4 py-2 rounded transition-colors ${
            generatedPrompt 
              ? 'bg-purple-500 hover:bg-purple-600 text-white' 
              : 'bg-gray-200 text-gray-500 cursor-not-allowed'
          }`}
        >
          Copy
        </button>
        <button 
          onClick={clearPrompt} 
          disabled={!generatedPrompt}
          className={`px-4 py-2 rounded transition-colors ${
            generatedPrompt 
              ? 'bg-red-500 hover:bg-red-600 text-white' 
              : 'bg-gray-200 text-gray-500 cursor-not-allowed'
          }`}
        >
          Clear
        </button>
      </div>
      
      {/* Generated Prompt */}
      <div className="mt-4">
        <label className="block text-sm font-medium text-gray-700 mb-1">Generated Prompt</label>
        <textarea 
          readOnly 
          value={generatedPrompt} 
          className="w-full p-3 border rounded min-h-24 bg-gray-50" 
          placeholder="Your generated prompt will appear here..."
        />
        
        {negativePrompt && (
          <div className="mt-2">
            <label className="block text-sm font-medium text-gray-700 mb-1">Negative Prompt</label>
            <textarea 
              readOnly 
              value={negativePrompt} 
              className="w-full p-3 border rounded min-h-12 bg-gray-50 border-red-200" 
            />
          </div>
        )}
      </div>
      
      {/* Saved Prompts */}
      {savedPrompts.length > 0 && (
        <div className="mt-4">
          <h3 className="font-semibold text-md mb-2 text-gray-700">Saved Prompts</h3>
          <div className="max-h-40 overflow-y-auto border rounded p-2 bg-gray-50">
            {savedPrompts.map((prompt, index) => (
              <div key={index} className="p-2 hover:bg-gray-100 text-sm border-b last:border-b-0">
                {prompt}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

