import React, { useState } from 'react';
import { FaInfoCircle } from 'react-icons/fa';

const AdvancedPromptGenerator = () => {
  // Core settings
  const [category, setCategory] = useState("Fantasy");
  const [style, setStyle] = useState("Digital Art");
  const [subject, setSubject] = useState("");
  const [mood, setMood] = useState("");
  const [composition, setComposition] = useState("");
  const [lighting, setLighting] = useState("");
  const [colorScheme, setColorScheme] = useState("");
  const [details, setDetails] = useState("");
  const [extraDetails, setExtraDetails] = useState("");

  // Camera settings
  const [showCamera, setShowCamera] = useState(false);
  const [camera, setCamera] = useState("");
  const [lens, setLens] = useState("");
  const [focalLength, setFocalLength] = useState("");
  const [aperture, setAperture] = useState("");
  const [shutterSpeed, setShutterSpeed] = useState("");
  const [iso, setIso] = useState("");
  const [effect, setEffect] = useState("");

  // Artist settings
  const [showArtistInfo, setShowArtistInfo] = useState(false);
  const [selectedArtists, setSelectedArtists] = useState([]);
  const [qualityModifiers, setQualityModifiers] = useState([]);

  // Output
  const [generatedPrompt, setGeneratedPrompt] = useState("");
  const [savedPrompts, setSavedPrompts] = useState([]);

  // New states for templates and AI Model
  const [templates, setTemplates] = useState([]);
  const [aiModel, setAiModel] = useState("Midjourney");
  const [modelSpecificSettings, setModelSpecificSettings] = useState({
    steps: 50,
    cfg: 7,
    sampler: "Euler a",
    seed: -1
  });

  // Available options
  const availableCategories = ["Fantasy", "Sci-Fi", "Nature", "Portrait", "Architecture", "Abstract", "Still Life"];
  const availableStyles = ["Digital Art", "Oil Painting", "Watercolor", "Photography", "3D Render", "Sketch", "Anime"];
  const availableMoods = ["Happy", "Mysterious", "Dark", "Peaceful", "Energetic", "Romantic", "Melancholic"];
  const availableCompositions = ["Close-up", "Wide Shot", "Bird's Eye View", "Low Angle", "Rule of Thirds", "Symmetrical", "Dynamic"];
  const availableLighting = ["Natural", "Studio", "Dramatic", "Soft", "Hard", "Backlit", "Cinematic"];
  const availableColorSchemes = ["Vibrant", "Monochromatic", "Pastel", "Dark", "Warm", "Cool", "Complementary"];
  const availableQualityModifiers = ["High Quality", "4K", "8K", "Detailed", "Sharp", "Professional", "Award Winning"];
  const availableArtists = [
    "Vincent van Gogh",
    "Leonardo da Vinci",
    "Pablo Picasso",
    "Claude Monet",
    "Salvador DalÃ­",
    "Frida Kahlo",
    "Gustav Klimt",
    "Andy Warhol",
    "Rembrandt",
    "Michelangelo"
  ];

  const saveAsTemplate = () => {
    const template = {
      name: "Template " + (templates.length + 1),
      settings: {
        category,
        style,
        mood,
        lighting,
        colorScheme,
        selectedArtists,
        qualityModifiers,
        aiModel,
        modelSpecificSettings
      }
    };
    setTemplates(prev => [...prev, template]);
  };

  const loadTemplate = (template) => {
    setCategory(template.settings.category);
    setStyle(template.settings.style);
    setMood(template.settings.mood);
    setLighting(template.settings.lighting);
    setColorScheme(template.settings.colorScheme);
    setSelectedArtists(template.settings.selectedArtists);
    setQualityModifiers(template.settings.qualityModifiers);
    setAiModel(template.settings.aiModel);
    setModelSpecificSettings(template.settings.modelSpecificSettings);
  };

  const generatePrompt = () => {
    let prompt = "";
    
    if (qualityModifiers.length > 0) {
      prompt = `${qualityModifiers.join(", ")}, `;
    }
    
    prompt += `${category} ${style} of ${subject}`;
    if (mood) prompt += `, ${mood} mood`;
    if (composition) prompt += `, ${composition} composition`;
    if (lighting) prompt += `, ${lighting} lighting`;
    if (colorScheme) prompt += `, ${colorScheme} colors`;
    if (details) prompt += `, ${details}`;
    
    if (showCamera) {
      if (camera) prompt += `, shot on ${camera}`;
      if (lens) prompt += ` with ${lens}`;
      if (focalLength) prompt += ` at ${focalLength}`;
      if (aperture) prompt += `, f/${aperture}`;
      if (shutterSpeed) prompt += `, ${shutterSpeed}`;
      if (iso) prompt += `, ISO ${iso}`;
      if (effect) prompt += `, ${effect} effect`;
    }
    
    if (selectedArtists.length > 0) {
      prompt += `, in the style of ${selectedArtists.join(" and ")}`;
    }
    
    if (extraDetails) prompt += `, ${extraDetails}`;

    if (aiModel === "Stable Diffusion") {
      prompt += `\n--steps ${modelSpecificSettings.steps} --cfg ${modelSpecificSettings.cfg}`;
      if (modelSpecificSettings.seed !== -1) {
        prompt += ` --seed ${modelSpecificSettings.seed}`;
      }
    }
    
    setGeneratedPrompt(prompt);
  };

  const toggleArtist = (artist) => {
    setSelectedArtists(prev =>
      prev.includes(artist)
        ? prev.filter(a => a !== artist)
        : [...prev, artist]
    );
  };

  const toggleQualityModifier = (modifier) => {
    setQualityModifiers(prev =>
      prev.includes(modifier)
        ? prev.filter(m => m !== modifier)
        : [...prev, modifier]
    );
  };

  const savePrompt = () => {
    if (generatedPrompt) {
      setSavedPrompts(prev => [...prev, generatedPrompt]);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">Advanced Prompt Generator</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Left Column - Settings */}
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold mb-4">Settings</h2>
          
          {/* Basic Settings */}
          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Category</label>
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="w-full p-2 border rounded"
            >
              {availableCategories.map((cat) => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>

          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Style</label>
            <select
              value={style}
              onChange={(e) => setStyle(e.target.value)}
              className="w-full p-2 border rounded"
            >
              {availableStyles.map((s) => (
                <option key={s} value={s}>{s}</option>
              ))}
            </select>
          </div>

          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Subject</label>
            <input
              type="text"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              placeholder="Enter the main subject"
              className="w-full p-2 border rounded"
            />
          </div>

          {/* Advanced Settings */}
          <h3 className="text-lg font-semibold mb-2">Advanced Settings</h3>
          
          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Mood</label>
            <select
              value={mood}
              onChange={(e) => setMood(e.target.value)}
              className="w-full p-2 border rounded"
            >
              <option value="">Select Mood</option>
              {availableMoods.map((m) => (
                <option key={m} value={m}>{m}</option>
              ))}
            </select>
          </div>

          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Composition</label>
            <select
              value={composition}
              onChange={(e) => setComposition(e.target.value)}
              className="w-full p-2 border rounded"
            >
              <option value="">Select Composition</option>
              {availableCompositions.map((comp) => (
                <option key={comp} value={comp}>{comp}</option>
              ))}
            </select>
          </div>

          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Lighting</label>
            <select
              value={lighting}
              onChange={(e) => setLighting(e.target.value)}
              className="w-full p-2 border rounded"
            >
              <option value="">Select Lighting</option>
              {availableLighting.map((light) => (
                <option key={light} value={light}>{light}</option>
              ))}
            </select>
          </div>

          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Color Scheme</label>
            <select
              value={colorScheme}
              onChange={(e) => setColorScheme(e.target.value)}
              className="w-full p-2 border rounded"
            >
              <option value="">Select Color Scheme</option>
              {availableColorSchemes.map((scheme) => (
                <option key={scheme} value={scheme}>{scheme}</option>
              ))}
            </select>
          </div>

          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Additional Details</label>
            <input
              type="text"
              value={details}
              onChange={(e) => setDetails(e.target.value)}
              placeholder="Enter additional details"
              className="w-full p-2 border rounded"
            />
          </div>

          {/* Camera Settings */}
          <div className="mb-3">
            <div className="flex items-center mb-2">
              <input
                type="checkbox"
                checked={showCamera}
                onChange={(e) => setShowCamera(e.target.checked)}
                className="mr-2"
              />
              <label className="text-sm font-medium text-gray-700">Show Camera Settings</label>
            </div>
            
            {showCamera && (
              <div className="grid grid-cols-2 gap-2">
                <div>
                  <select
                    value={camera}
                    onChange={(e) => setCamera(e.target.value)}
                    className="w-full p-2 border rounded"
                  >
                    <option value="">Select Camera</option>
                    <option>Canon EOS R5</option>
                    <option>Sony A7R IV</option>
                    <option>Nikon Z9</option>
                    <option>Fujifilm X-T4</option>
                    <option>Hasselblad X1D II</option>
                  </select>
                </div>
                <div>
                  <select
                    value={lens}
                    onChange={(e) => setLens(e.target.value)}
                    className="w-full p-2 border rounded"
                  >
                    <option value="">Select Lens</option>
                    <option>50mm f/1.4</option>
                    <option>24-70mm f/2.8</option>
                    <option>70-200mm f/2.8</option>
                    <option>85mm f/1.2</option>
                    <option>35mm f/1.4</option>
                  </select>
                </div>
                <div>
                  <select
                    value={focalLength}
                    onChange={(e) => setFocalLength(e.target.value)}
                    className="w-full p-2 border rounded"
                  >
                    <option value="">Select Focal Length</option>
                    <option>24mm</option>
                    <option>35mm</option>
                    <option>50mm</option>
                    <option>85mm</option>
                    <option>135mm</option>
                  </select>
                </div>
                <div>
                  <select
                    value={aperture}
                    onChange={(e) => setAperture(e.target.value)}
                    className="w-full p-2 border rounded"
                  >
                    <option value="">Select Aperture</option>
                    <option>f/1.4</option>
                    <option>f/2.8</option>
                    <option>f/4</option>
                    <option>f/5.6</option>
                    <option>f/8</option>
                    <option>f/11</option>
                  </select>
                </div>
                <div>
                  <select
                    value={shutterSpeed}
                    onChange={(e) => setShutterSpeed(e.target.value)}
                    className="w-full p-2 border rounded"
                  >
                    <option value="">Select Shutter Speed</option>
                    <option>1/1000</option>
                    <option>1/500</option>
                    <option>1/250</option>
                    <option>1/125</option>
                    <option>1/60</option>
                    <option>1/30</option>
                  </select>
                </div>
                <div>
                  <select
                    value={iso}
                    onChange={(e) => setIso(e.target.value)}
                    className="w-full p-2 border rounded"
                  >
                    <option value="">Select ISO</option>
                    <option>100</option>
                    <option>200</option>
                    <option>400</option>
                    <option>800</option>
                    <option>1600</option>
                    <option>3200</option>
                  </select>
                </div>
                <div className="col-span-2">
                  <select
                    value={effect}
                    onChange={(e) => setEffect(e.target.value)}
                    className="w-full p-2 border rounded"
                  >
                    <option value="">Select Effect</option>
                    <option>depth of field</option>
                    <option>motion blur</option>
                    <option>HDR</option>
                    <option>long exposure</option>
                    <option>bokeh</option>
                  </select>
                </div>
              </div>
            )}
          </div>

          {/* Quality Modifiers */}
          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Quality Modifiers</label>
            <div className="flex flex-wrap gap-2">
              {availableQualityModifiers.map((modifier) => (
                <button
                  key={modifier}
                  onClick={() => toggleQualityModifier(modifier)}
                  className={`px-3 py-1 rounded text-sm ${
                    qualityModifiers.includes(modifier)
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-200 text-gray-700'
                  }`}
                >
                  {modifier}
                </button>
              ))}
            </div>
          </div>

          {/* Templates */}
          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Templates</label>
            <div className="flex gap-2">
              <button
                onClick={saveAsTemplate}
                className="bg-green-500 text-white px-3 py-1 rounded text-sm hover:bg-green-600"
              >
                Save as Template
              </button>
              {templates.map((template, index) => (
                <button
                  key={index}
                  onClick={() => loadTemplate(template)}
                  className="bg-blue-100 text-blue-700 px-3 py-1 rounded text-sm hover:bg-blue-200"
                >
                  {template.name}
                </button>
              ))}
            </div>
          </div>

          {/* AI Model Settings */}
          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">AI Model</label>
            <select
              value={aiModel}
              onChange={(e) => setAiModel(e.target.value)}
              className="w-full p-2 border rounded"
            >
              <option>Midjourney</option>
              <option>Stable Diffusion</option>
              <option>DALL-E</option>
            </select>
          </div>

          {aiModel === "Stable Diffusion" && (
            <div className="mb-3 grid grid-cols-2 gap-2">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Steps</label>
                <input
                  type="number"
                  value={modelSpecificSettings.steps}
                  onChange={(e) => setModelSpecificSettings(prev => ({...prev, steps: parseInt(e.target.value)}))}
                  className="w-full p-2 border rounded"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">CFG Scale</label>
                <input
                  type="number"
                  value={modelSpecificSettings.cfg}
                  onChange={(e) => setModelSpecificSettings(prev => ({...prev, cfg: parseInt(e.target.value)}))}
                  className="w-full p-2 border rounded"
                />
              </div>
            </div>
          )}

          {/* Artist Inspiration */}
          <div className="mb-3">
            <div className="flex items-center justify-between mb-2">
              <div className="flex items-center">
                <input
                  type="checkbox"
                  checked={showArtistInfo}
                  onChange={(e) => setShowArtistInfo(e.target.checked)}
                  className="mr-2"
                />
                <label className="text-sm font-medium text-gray-700">Artist Inspiration</label>
              </div>
              <FaInfoCircle className="text-gray-500" title="Select artists to inspire the style" />
            </div>
            
            {showArtistInfo && (
              <div className="flex flex-wrap gap-2">
                {availableArtists.map((artist) => (
                  <button
                    key={artist}
                    onClick={() => toggleArtist(artist)}
                    className={`px-3 py-1 rounded text-sm ${
                      selectedArtists.includes(artist)
                        ? 'bg-blue-500 text-white'
                        : 'bg-gray-200 text-gray-700'
                    }`}
                  >
                    {artist}
                  </button>
                ))}
              </div>
            )}
          </div>

          <div className="mb-3">
            <label className="block text-sm font-medium text-gray-700 mb-1">Extra Details</label>
            <textarea
              value={extraDetails}
              onChange={(e) => setExtraDetails(e.target.value)}
              placeholder="Any additional details or modifications"
              className="w-full p-2 border rounded"
              rows="3"
            />
          </div>

          <button
            onClick={generatePrompt}
            className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
          >
            Generate Prompt
          </button>
        </div>

        {/* Right Column - Preview */}
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold mb-4">Preview</h2>
          
          <div className="mb-4">
            <textarea
              value={generatedPrompt}
              readOnly
              className="w-full p-2 border rounded bg-gray-50"
              rows="6"
            />
          </div>

          <div className="flex justify-between">
            <button
              onClick={() => navigator.clipboard.writeText(generatedPrompt)}
              className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
            >
              Copy to Clipboard
            </button>
            <button
              onClick={savePrompt}
              className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
            >
              Save Prompt
            </button>
          </div>

          {savedPrompts.length > 0 && (
            <div className="mt-6">
              <h3 className="text-lg font-semibold mb-2">Saved Prompts</h3>
              <div className="space-y-2">
                {savedPrompts.map((prompt, index) => (
                  <div key={index} className="p-2 bg-gray-50 rounded">
                    {prompt}
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdvancedPromptGenerator;
